            test: /[\\/]node_modules[\\/](framer-motion)[\\/]/,
            name: "framer-motion",
            chunks: "async",
            priority: 35,
            enforce: true,
          },
          mdEditor: {
            test: /[\\/]node_modules[\\/](@uiw)[\\/]/,
            name: "md-editor",
            chunks: "async",
            priority: 35,
            enforce: true,
          },
        },
      };
    }
    return config;
  },
  async headers() {
