  "orientation": "any",
  "prefer_related_applications": false
}

import fs from "fs";
import https from "https";
import path from "path";

import currencies from "../src/lib/currencies/currencies.json";

const downloadImage = (url: string, filepath: string): Promise => {
  return new Promise((resolve, reject) => {
    if (!url) {
      console.log(`Skipping empty URL for ${filepath}`);
      resolve();
      return;
    }

    https
      .get(url, (response) => {
        if (response.statusCode === 200) {
          const fileStream = fs.createWriteStream(filepath);
          response.pipe(fileStream);
          fileStream.on("finish", () => {
            fileStream.close();
            resolve();
          });
        } else {
          reject(new Error(`Failed to download ${url}: ${response.statusCode}`));
        }
      })
      .on("error", reject);
  });
};

async function downloadCurrencyImages() {
  const imagesDir = path.join(process.cwd(), "public", "currencies");

  // Create directory if it doesn't exist
  if (!fs.existsSync(imagesDir)) {
    fs.mkdirSync(imagesDir, { recursive: true });
  }

  for (const currency of currencies) {
    if (!currency.image_url) continue;

    const filename = currency.name.toLowerCase().replace(/[^a-z0-9]/g, "_") + ".png";
    const filepath = path.join(imagesDir, filename);

    try {
      await downloadImage(currency.image_url, filepath);
      console.log(`Downloaded ${currency.name} image`);
    } catch (error) {
      console.error(`Error downloading ${currency.name} image:`, error);
    }
  }
}

downloadCurrencyImages().catch(console.error);

import fs from 'fs';
import path from 'path';
import { itemsService } from '../src/services/items-service';
import type { EquipmentSlot } from '../src/lib/supabase/types';

const IMAGES_DIR = path.join(process.cwd(), 'public/items');
const URL_MAPPING_PATH = path.join(process.cwd(), 'public/data/item-image-mapping.json');

const headers = {
  'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
  'Accept': 'image/webp,image/apng,image/*,*/*;q=0.8',
  'Accept-Language': 'en-US,en;q=0.9',
  'Cache-Control': 'no-cache',
  'Pragma': 'no-cache',
  'Referer': 'https://poe2db.tw/'
};

async function downloadImage(url: string, filename: string): Promise {
  try {
    const response = await fetch(url, { headers });
    if (!response.ok) {
      console.error(`Failed to download ${url}: ${response.status} ${response.statusText}`);
      return false;
    }

    const buffer = await response.arrayBuffer();
    fs.writeFileSync(filename, Buffer.from(buffer));
    return true;
  } catch (error) {
    console.error(`Error downloading ${url}:`, error);
    return false;
  }
}

async function main() {
  if (!fs.existsSync(IMAGES_DIR)) {
    fs.mkdirSync(IMAGES_DIR, { recursive: true });
  }

  const ITEMS_DATA_PATH = path.join(process.cwd(), 'public/data/items.json');
  const uniqueUrls = new Set();
  const urlMapping: Record = {};

  // Load existing mapping if it exists
  if (fs.existsSync(URL_MAPPING_PATH)) {
    const existingMapping = JSON.parse(fs.readFileSync(URL_MAPPING_PATH, 'utf-8'));
    Object.assign(urlMapping, existingMapping);
  }

  // Read and parse items data
  const itemsData = JSON.parse(fs.readFileSync(ITEMS_DATA_PATH, 'utf-8')) as Record<string, Record<string, { items: Array<{ icon?: string }> }>>;

  // Collect all unique image URLs
  for (const categoryGroup of Object.values(itemsData)) {
    for (const category of Object.values(categoryGroup)) {
      const items = category.items || [];
      for (const item of items) {
        if (item.icon && item.icon.includes('cdn.poe2db.tw')) {
          uniqueUrls.add(item.icon);
        }
      }
    }
  }

  console.log(`Found ${uniqueUrls.size} unique image URLs to process`);

  let processed = 0;
  for (const url of uniqueUrls) {
    const urlHash = Buffer.from(url).toString('base64url');
    const ext = path.extname(url) || '.webp';
    const filename = `${urlHash}${ext}`;
    const localPath = path.join(IMAGES_DIR, filename);

    if (!fs.existsSync(localPath)) {
      const success = await downloadImage(url, localPath);
      if (success) {
        urlMapping[url] = `/items/${filename}`;
        console.log(`Downloaded ${++processed}/${uniqueUrls.size}: ${url}`);
      }
    } else {
      urlMapping[url] = `/items/${filename}`;
      console.log(`Skipped existing ${++processed}/${uniqueUrls.size}: ${url}`);
    }

    // Save mapping after each file to preserve progress
    fs.writeFileSync(URL_MAPPING_PATH, JSON.stringify(urlMapping, null, 2));
  }

  console.log('Download complete. URL mapping saved to:', URL_MAPPING_PATH);
}

main().catch(console.error);

import fs from "fs";
import path from "path";
import type { Browser, Page } from "puppeteer";
import puppeteer from "puppeteer";

interface LadderEntry {
  rank: number;
  account: string;
  character: string;
  class: string;
  level: number;
  experience: number;
}

interface ClassDistribution {
  className: string;
  count: number;
  percentage: number;
}

interface LadderStats {
  timestamp: string;
  ladders: {
    [key: string]: {
      total: number;
      distribution: ClassDistribution[];
    };
  };
  overall: {
    total: number;
    distribution: ClassDistribution[];
  };
}

const LADDER_URLS = {
  Standard: "https://pathofexile2.com/ladder/Standard",
  Hardcore: "https://pathofexile2.com/ladder/Hardcore",
  SSF: "https://pathofexile2.com/ladder/Solo%2520Self-Found",
  "HC SSF": "https://pathofexile2.com/ladder/Hardcore%2520SSF",
};

const MAX_RETRIES = 3;
const RETRY_DELAY = 2000;

async function retry(fn: () => Promise, retries = MAX_RETRIES): Promise {
  try {
    return await fn();
  } catch (error) {
    if (retries > 0) {
      await new Promise((resolve) => setTimeout(resolve, RETRY_DELAY));
      return retry(fn, retries - 1);
    }
    throw error;
  }
}

async function setupPage(browser: Browser): Promise {
  const page = await browser.newPage();
  await page.setViewport({ width: 1200, height: 800 });
  return page;
}

async function scrapeLadder(page: Page, url: string): Promise {
  const entries: LadderEntry[] = [];

  try {
    await retry(async () => {
      await page.goto(url, {
        waitUntil: "networkidle0",
        timeout: 30000,
      });
    });

    const rows = await page.evaluate(() => {
      const tableRows = document.querySelectorAll(".league-ladder__entry");
      return Array.from(tableRows).map((row) => {
        const cells = row.querySelectorAll("td");
        return {
          rank: parseInt(cells[0]?.textContent || "0", 10),
          account: cells[1]?.querySelector("a")?.textContent?.trim() || "",
          character: cells[2]?.textContent?.trim() || "",
          class: cells[3]?.textContent?.trim() || "",
          level: parseInt(cells[4]?.textContent || "0", 10),
          experience: parseInt(cells[5]?.textContent?.replace(/,/g, "") || "0", 10),
        };
      });
    });

    entries.push(...rows);
  } catch (error) {
    console.error(`Error scraping ladder ${url}:`, error);
  }

  return entries;
}

function calculateDistribution(entries: LadderEntry[]): ClassDistribution[] {
  const classCounts = entries.reduce((acc, entry) => {
    acc[entry.class] = (acc[entry.class] || 0) + 1;
    return acc;
  }, {} as Record);

  const total = entries.length;
  const distribution = Object.entries(classCounts)
    .map(([className, count]) => ({
      className,
      count,
      percentage: Math.round(((count / total) * 100) * 10) / 10,
    }))
    .sort((a, b) => b.count - a.count);

  return distribution;
}

export async function scrapeLadderStats(): Promise {
  const browser = await puppeteer.launch({
    headless: true,
    args: ["--no-sandbox", "--disable-setuid-sandbox"],
  });
  const page = await setupPage(browser);

  try {
    const ladderData: { [key: string]: LadderEntry[] } = {};
    for (const [name, url] of Object.entries(LADDER_URLS)) {
      console.log(`Scraping ${name} ladder...`);
      ladderData[name] = await scrapeLadder(page, url);
    }

    const allEntries = Object.values(ladderData).flat();
    const stats: LadderStats = {
      timestamp: new Date().toISOString(),
      ladders: {},
      overall: {
        total: allEntries.length,
        distribution: calculateDistribution(allEntries),
      },
    };

    for (const [name, entries] of Object.entries(ladderData)) {
      stats.ladders[name] = {
        total: entries.length,
        distribution: calculateDistribution(entries),
      };
    }

    return stats;
  } finally {
    await browser.close();
  }
}

export async function saveLadderStats(stats: LadderStats): Promise {
  const projectRoot = path.resolve(__dirname, "..");
  const dataDir = path.join(projectRoot, "public", "data");

  if (!fs.existsSync(dataDir)) {
    fs.mkdirSync(dataDir, { recursive: true });
  }

  const statsPath = path.join(dataDir, "ladder-stats.json");
  await fs.promises.writeFile(statsPath, JSON.stringify(stats, null, 2));
}

export type { LadderEntry, LadderStats, ClassDistribution };

import fs from "fs";
import path from "path";
import type { Browser, Page } from "puppeteer";
import puppeteer from "puppeteer";

interface NewsPost {
  id: string;
  title: string;
  date: string;
  url: string;
  content: string;
  author: string;
  lastBumped?: string;
  replies?: number;
  lastReplyBy?: string;
  lastReplyDate?: string;
  type: "announcement" | "patch-note";
  imageUrl?: string;
}

type PatchNote = NewsPost;

const MAX_RETRIES = 3;
const RETRY_DELAY = 2000;

async function retry(fn: () => Promise, retries = MAX_RETRIES): Promise {
  try {
    return await fn();
  } catch (error) {
    if (retries > 0) {
      await new Promise((resolve) => setTimeout(resolve, RETRY_DELAY));
      return retry(fn, retries - 1);
    }
    throw error;
  }
}

async function setupPage(browser: Browser): Promise {
  const page = await browser.newPage();
  await page.setViewport({ width: 1200, height: 800 });
  await page.setRequestInterception(true);

  // Optimize by blocking unnecessary resources
  page.on("request", (request: any) => {
    const resourceType = request.resourceType();
    if (["image", "stylesheet", "font", "media"].includes(resourceType)) {
      request.abort();
    } else {
      request.continue();
    }
  });

  return page;
}

interface ThreadData {
  id: string;
  title: string;
  url: string;
  date: string;
  author: string;
  replies: number;
  lastReplyBy?: string;
  lastReplyDate?: string;
}

interface PostContent {
  content: string;
  imageUrl?: string;
}

async function scrapeForumThreads(
  page: Page,
  forumId: string,
  type: "announcement" | "patch-note"
): Promise {
  const posts: NewsPost[] = [];

  try {
    await retry(async () => {
      await page.goto(`https://www.pathofexile.com/forum/view-forum/${forumId}`, {
        waitUntil: "networkidle0",
        timeout: 30000,
      });
    });

    console.log(`Scraping ${type} from forum ${forumId}...`);
    const threads = (await page.evaluate((postType: "announcement" | "patch-note") => {
      const threadElements = document.querySelectorAll(".forumTable tbody tr");
      return Array.from(threadElements)
        .map((thread) => {
          const titleElement = thread.querySelector(".thread .title a");
          const dateElement = thread.querySelector(".postBy .post_date");
          const authorElement = thread.querySelector(".postBy .profile-link.post_by_account a");
          const repliesElement = thread.querySelector(".views span");
          const lastReplyByElement = thread.querySelector(".last_post .profile-link a");
          const lastReplyDateElement = thread.querySelector(".last_post .post_date a");
          const threadId = titleElement?.getAttribute("href")?.split("/")[3] || "";

          return {
            id: threadId,
            title: titleElement?.textContent?.trim() || "",
            url: titleElement?.getAttribute("href") || "",
            date: dateElement?.textContent?.replace(",", "").trim() || "",
            author: authorElement?.textContent?.trim() || "",
            replies: parseInt(repliesElement?.textContent || "0", 10),
            lastReplyBy: lastReplyByElement?.textContent?.trim(),
            lastReplyDate: lastReplyDateElement?.textContent?.trim(),
          };
        })
        .filter((thread) => {
          const isStaffPost = !!thread.author;
          if (!isStaffPost) return false;

          const title = thread.title.toLowerCase();
          if (postType === "patch-note") {
            return isStaffPost && (title.includes("patch notes") || title.includes("hotfix"));
          }
          // For announcements, include all staff posts except patch notes/hotfixes
          return isStaffPost && !(title.includes("patch notes") || title.includes("hotfix"));
        });
    }, type)) as ThreadData[];

    for (const thread of threads) {
      if (!thread.url) continue;

      try {
        await retry(async () => {
          await page.goto(`https://www.pathofexile.com${thread.url}`, {
            waitUntil: "networkidle0",
            timeout: 30000,
          });
        });

        const { content, imageUrl } = (await page.evaluate(() => {
          const staffPost =
            document.querySelector("tr.newsPost") || document.querySelector("tr.staff");
          if (!staffPost) return { content: "" };

          const contentElement = staffPost.querySelector(".content");
          const imageElement = staffPost.querySelector(".content img") as HTMLImageElement;

          return {
            content: contentElement?.innerHTML || "",
            imageUrl: imageElement?.src,
          };
        })) as PostContent;

        if (content) {
          posts.push({
            id: thread.id,
            title: thread.title,
            date: thread.date,
            url: `https://www.pathofexile.com${thread.url}`,
            content,
            author: thread.author,
            replies: thread.replies,
            lastReplyBy: thread.lastReplyBy,
            lastReplyDate: thread.lastReplyDate,
            type,
            imageUrl,
          });
        }
      } catch (error) {
        console.error(`Error processing thread ${thread.url}:`, error);
        continue;
      }
    }
  } catch (error) {
    console.error(`Error scraping ${type}:`, error);
  }

  return posts;
}

export async function scrapePatchNotes(): Promise {
  const browser = await puppeteer.launch({
    headless: true,
    args: ["--no-sandbox", "--disable-setuid-sandbox"],
  });
  const page = await setupPage(browser);

  try {
    const announcements = await scrapeForumThreads(page, "2211", "announcement");
    const patchNotes = await scrapeForumThreads(page, "2212", "patch-note");

    const allPosts = [...announcements, ...patchNotes];

    // Sort by date, newest first
    return allPosts.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
  } finally {
    await browser.close();
  }
}

export async function savePatchNotes(posts: NewsPost[]): Promise {
  const projectRoot = path.resolve(__dirname, "..");
  const dataDir = path.join(projectRoot, "public", "data");

  // Ensure data directory exists
  if (!fs.existsSync(dataDir)) {
    fs.mkdirSync(dataDir, { recursive: true });
  }

  // Filter and save patch notes
  const patchNotes = posts.filter((post) => post.type === "patch-note");
  const patchNotesPath = path.join(dataDir, "patch-notes.json");
  await fs.promises.writeFile(patchNotesPath, JSON.stringify(patchNotes, null, 2));
}

export async function saveAnnouncements(posts: NewsPost[]): Promise {
  const projectRoot = path.resolve(__dirname, "..");
  const dataDir = path.join(projectRoot, "public", "data");

  // Ensure data directory exists
  if (!fs.existsSync(dataDir)) {
    fs.mkdirSync(dataDir, { recursive: true });
  }

  // Filter and save announcements
  const announcements = posts.filter((post) => post.type === "announcement");
  const announcementsPath = path.join(dataDir, "announcements.json");
  await fs.promises.writeFile(announcementsPath, JSON.stringify(announcements, null, 2));
}

