interface ItemSkill {
  name: string;
  icon: string;
  url: string;
  stats?: SkillStat[];
  description?: string;
}

interface SkillStat {
  name: string;
  value: string;
}

interface ItemBase {
  name: string;
  icon: string;
  url: string;
  category: string;
  requirements?: { level?: number };
  grantedSkill?: ItemSkill;
  modifiers?: string[];
  description?: string;
  tabSource?: string;
}

interface ModifierTier {
  name: string;
  level: number;
  stats: string[];
}

interface ItemModifier {
  family: string;
  type: "prefix" | "suffix" | "socketable";
  tags: string[];
  tiers: ModifierTier[];
}

interface ItemCategory {
  name: string;
  url: string;
  items: ItemBase[];
  modifiers?: ItemModifier[];
}

interface ItemGroup {
  name: string;
  categories: string[];
}

