"use server";

import type { CookieOptions } from "@supabase/ssr";

import { cookies } from "next/headers";

import { createClient } from "~/lib/supabase/server";

export const getServerClient = async () => {
  const cookieStore = await cookies();
  return createClient({
    get: (name: string) => cookieStore.get(name),
    set: (opts: { name: string; value: string } & CookieOptions) => cookieStore.set(opts),
  });
};

"use server";

import type { POEOAuthConfig, POEScope } from "@/types/poe-api";

import { revalidatePath } from "next/cache";
import { cookies } from "next/headers";

import { getServerClient } from "~/app/_actions/supabase";
import { POEClient } from "~/lib/poe/client";
import type { POEAccountData } from "~/lib/supabase/types";

const POE_CONFIG: POEOAuthConfig = {
  clientId: process.env.NEXT_PUBLIC_POE_CLIENT_ID!,
  clientSecret: process.env.POE_CLIENT_SECRET!,
  redirectUri: `${process.env.NEXT_PUBLIC_APP_URL}/api/auth/callback?provider=poe`,
  scopes: ["account:profile", "account:stashes", "account:characters"] as POEScope[],
  isConfidentialClient: true,
};

export async function connectPOEAccount() {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) throw new Error("Must be logged in to connect POE account");

  await supabase
    .from("profiles")
    .update({
      poe_account: {
        connected: false,
        accountName: undefined,
        lastSync: new Date().toISOString(),
      } satisfies POEAccountData,
    })
    .eq("id", user.id);

  // Generate auth URL
  const poeClient = new POEClient(POE_CONFIG);
  const state = crypto.randomUUID();
  const codeVerifier = generateCodeVerifier();
  const authUrl = await poeClient.generateAuthUrl(state, codeVerifier);

  // Store code verifier in cookie for callback
  const cookieStore = await cookies();
  cookieStore.set("poe_code_verifier", codeVerifier, {
    httpOnly: true,
    secure: process.env.NODE_ENV === "production",
    sameSite: "lax",
    path: "/",
    maxAge: 60 * 5, // 5 minutes
  });

  revalidatePath("/profile");
  return authUrl;
}

export async function disconnectPOEAccount() {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) throw new Error("Must be logged in to disconnect POE account");

  const { error } = await supabase
    .from("profiles")
    .update({
      poe_account: null,
      poe_refresh_token: null,
    })
    .eq("id", user.id);

  if (error) throw error;

  revalidatePath("/profile");
}

export async function getPOEAccountStatus() {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) throw new Error("Must be logged in to check POE account status");

  const { data, error } = await supabase
    .from("profiles")
    .select("poe_account")
    .eq("id", user.id)
    .single();

  if (error) throw error;

  return data?.poe_account as POEAccountData | null;
}

export async function refreshPOEProfile() {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) throw new Error("Must be logged in to refresh POE profile");

  const { data: profile } = await supabase
    .from("profiles")
    .select("poe_account, poe_refresh_token")
    .eq("id", user.id)
    .single();

  if (!profile?.poe_refresh_token) {
    throw new Error("POE account not connected");
  }

  const poeClient = new POEClient({
    ...POE_CONFIG,
    isConfidentialClient: true,
  });

  // The client will handle storing the tokens in the profile
  const poeProfile = await poeClient.getProfile();

  revalidatePath("/profile");
  return poeProfile;
}

function generateCodeVerifier() {
  const array = new Uint8Array(32);
  crypto.getRandomValues(array);
  return btoa(String.fromCharCode(...array))
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");
}

"use server";

import { revalidatePath } from "next/cache";

import { validateName } from "~/utils/validation";

import { getServerClient } from "~/app/_actions/supabase";

export type ProfileUpdateResponse = {
  success: boolean;
  error?: string;
};

export async function updateProfile(name: string): Promise {
  // Validate name
  const validation = validateName(name);
  if (!validation.valid) {
    return { success: false, error: validation.error };
  }

  const supabase = await getServerClient();

  const {
    data: { user },
    error: userError,
  } = await supabase.auth.getUser();

  if (userError) throw userError;
  if (!user) throw new Error("Not authenticated");

  // Update auth metadata
  const { error: updateError } = await supabase.auth.updateUser({
    data: { name },
  });

  if (updateError) {
    return { success: false, error: updateError.message };
  }

  // Update profile
  const { error: profileError } = await supabase
    .from("profiles")
    .update({ name })
    .eq("id", user.id);

  if (profileError) {
    return { success: false, error: profileError.message };
  }

  revalidatePath("/profile");
  return { success: true };
}

export async function updatePassword(newPassword: string) {
  const supabase = await getServerClient();

  const { error } = await supabase.auth.updateUser({
    password: newPassword,
  });

  if (error) throw error;
}

export async function deleteAccount() {
  const supabase = await getServerClient();

  const {
    data: { user },
    error: userError,
  } = await supabase.auth.getUser();

  if (userError) throw userError;
  if (!user) throw new Error("Not authenticated");

  // Delete profile (cascades to other data via DB triggers)
  const { error: deleteError } = await supabase.from("profiles").delete().eq("id", user.id);

  if (deleteError) throw deleteError;

  // Sign out after deletion
  await supabase.auth.signOut();
}

"use server";

import { getServerClient } from "~/app/_actions/supabase";
import type { Database } from "~/lib/supabase/types";
import type { Activity, ActivityType } from "~/types/activity";

export async function logActivity(
  type: ActivityType,
  title: string,
  description: string,
  metadata?: any
) {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  const { error } = await supabase.from("activities").insert({
    user_id: user.id,
    type,
    title,
    description,
    metadata,
  });

  if (error) throw error;
}

export async function getRecentActivities(limit = 10, offset = 0): Promise {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  // Get one extra item to determine if there are more
  const { data, error } = await supabase
    .from("activities")
    .select("*")
    .eq("user_id", user.id)
    .order("created_at", { ascending: false })
    .range(offset, offset + limit);

  if (error) throw error;
  return data as Activity[];
}

"use server";

import { revalidatePath } from "next/cache";

import { getServerClient } from "~/app/_actions/supabase";
import type { Database } from "~/lib/supabase/types";

export type BuildConfig = Database["public"]["Tables"]["build_configs"]["Row"];
export type BuildConfigInsert = Database["public"]["Tables"]["build_configs"]["Insert"];

export async function getBuildConfigs(buildId: string) {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  const { data, error } = await supabase.from("build_configs").select("*").eq("build_id", buildId);

  if (error) throw error;
  return data || [];
}

export async function createBuildConfig(config: BuildConfigInsert) {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  const { data, error } = await supabase.from("build_configs").insert([config]).select().single();

  if (error) throw error;

  revalidatePath(`/build-planner/${config.build_id}`);
  revalidatePath(`/build-planner/${config.build_id}/stats`);

  return data;
}

export async function updateBuildConfig(id: string, updates: Partial) {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  const { data, error } = await supabase
    .from("build_configs")
    .update(updates)
    .eq("id", id)
    .select()
    .single();

  if (error) throw error;

  revalidatePath(`/build-planner/${data.build_id}`);
  revalidatePath(`/build-planner/${data.build_id}/stats`);

  return data;
}

export async function deleteBuildConfig(id: string, buildId: string) {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  const { error } = await supabase.from("build_configs").delete().eq("id", id);

  if (error) throw error;

  revalidatePath(`/build-planner/${buildId}`);
  revalidatePath(`/build-planner/${buildId}/stats`);

  return true;
}

"use server";

import { revalidatePath } from "next/cache";

import type { Database } from "~/lib/supabase/types";

import { type CreateBuildData, createBuild, updateBuild } from "./builds";

type VisibilityType = Database["public"]["Enums"]["visibility_type"];

export type BuildFormData = Omit;

export interface BuildFormResponse {
  success: boolean;
  buildId: string;
  error?: string;
}

export async function handleNewBuildSubmit(
  formData: Partial
): Promise {
  try {
    if (!formData.name) {
      return { success: false, buildId: "", error: "Build name is required" };
    }

    // Transform partial form data to required CreateBuildData
    // Ensure visibility is either private or unlisted
    const visibility: VisibilityType = formData.visibility === "unlisted" ? "unlisted" : "private";

    const buildData: CreateBuildData = {
      name: formData.name,
      description: formData.description || "",
      visibility,
      poe_class: formData.poe_class || "",
      level: formData.level || 1,
      notes: formData.notes || "",
      is_template: formData.is_template || false,
      parent_build_id: formData.parent_build_id,
      version: formData.version || "1.0.0",
      tags: formData.tags || [],
      slug: formData.slug,
    };

    const newBuild = await createBuild(buildData);
    if (!newBuild) {
      return { success: false, buildId: "", error: "Failed to create build" };
    }

    // Revalidate paths
    revalidatePath("/build-planner");
    revalidatePath(`/build-planner/${newBuild.slug || newBuild.id}`);
    revalidatePath(`/build-planner/${newBuild.id}`);

    return { success: true, buildId: newBuild.slug || newBuild.id };
  } catch (error) {
    console.error("Error in handleNewBuildSubmit:", error);
    return {
      success: false,
      buildId: "",
      error: error instanceof Error ? error.message : "Failed to create build",
    };
  }
}

export async function handleBuildSubmit(
  buildId: string,
  formData: Partial
): Promise {
  try {
    if (!formData.name) {
      return { success: false, buildId, error: "Build name is required" };
    }

    const buildData = {
      name: formData.name,
      description: formData.description || "",
      visibility: formData.visibility,
      poe_class: formData.poe_class || "",
      level: formData.level || 1,
      notes: formData.notes || "",
      is_template: formData.is_template || false,
      parent_build_id: formData.parent_build_id,
      version: formData.version || "1.0.0",
      tags: formData.tags || [],
      slug: formData.slug,
    };

    const updatedBuild = await updateBuild(buildId, buildData);
    if (!updatedBuild) {
      return { success: false, buildId, error: "Failed to update build" };
    }

    // Revalidate all possible paths
    revalidatePath("/build-planner");
    revalidatePath(`/build-planner/${buildId}`);
    revalidatePath(`/build-planner/${buildId}/edit`);
    revalidatePath(`/build-planner/${updatedBuild.id}`);
    revalidatePath(`/build-planner/${updatedBuild.id}/edit`);
    if (updatedBuild.slug) {
      revalidatePath(`/build-planner/${updatedBuild.slug}`);
      revalidatePath(`/build-planner/${updatedBuild.slug}/edit`);
    }

    // Wait for revalidation
    await new Promise((resolve) => setTimeout(resolve, 1000));

    return { success: true, buildId: updatedBuild.slug || updatedBuild.id };
  } catch (error) {
    console.error("Error in handleBuildSubmit:", error);
    return {
      success: false,
      buildId,
      error: error instanceof Error ? error.message : "Failed to update build",
    };
  }
}

"use server";

import { revalidatePath } from "next/cache";
import { headers } from "next/headers";

import { generateSlug } from "~/utils/slug";

import { getServerClient } from "~/app/_actions/supabase";
import type { Database } from "~/lib/supabase/types";

import { logActivity } from "./activities";

export type Build = Database["public"]["Tables"]["builds"]["Row"];
export type BuildInsert = Database["public"]["Tables"]["builds"]["Insert"];

export type VisibilityType = "private" | "unlisted" | "public";
export interface BuildOptions {
  visibility?: VisibilityType | "all";
  includeOwn?: boolean;
  cache?: RequestCache;
}

export type CreateBuildData = Omit;
export type UpdateBuildData = Partial;

export interface BuildWithRelations extends Build {
  equipment?: Database["public"]["Tables"]["equipment"]["Row"][];
  skill_gems?: Database["public"]["Tables"]["skill_gems"]["Row"][];
  build_configs?: Database["public"]["Tables"]["build_configs"]["Row"][];
}

// Helper to check if string is a valid UUID
const isValidUUID = (str: string) => {
  const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
  return uuidRegex.test(str);
};

// Unified function to find build by id or slug
async function findBuild(supabase: any, idOrSlug: string, selectQuery = "*") {
  // Try by slug first
  const { data: slugData, error: slugError } = await supabase
    .from("builds")
    .select(selectQuery)
    .eq("slug", idOrSlug)
    .maybeSingle();

  if (!slugError && slugData) {
    return slugData;
  }

  // If not found by slug and input is a valid UUID, try by id
  if (isValidUUID(idOrSlug)) {
    const { data: idData, error: idError } = await supabase
      .from("builds")
      .select(selectQuery)
      .eq("id", idOrSlug)
      .maybeSingle();

    if (!idError && idData) {
      return idData;
    }
  }

  return null;
}

export async function getBuild(idOrSlug: string): Promise {
  const supabase = await getServerClient();

  try {
    const {
      data: { user },
    } = await supabase.auth.getUser();

    const query = `
      id,
      name,
      description,
      visibility,
      slug,
      poe_class,
      level,
      notes,
      is_template,
      parent_build_id,
      version,
      tags,
      created_at,
      updated_at,
      user_id,
      equipment:equipment(*),
      skill_gems:skill_gems(*),
      build_configs:build_configs(*)
    `;

    const build = await findBuild(supabase, idOrSlug, query);
    if (!build) return null;

    // Check visibility and ownership before returning
    const isOwner = user && build.user_id === user.id;
    const isPublic = build.visibility === "public";
    const canAccess = isOwner || isPublic;

    return canAccess ? build : null;
  } catch (error) {
    console.error("Error in getBuild:", error);
    return null;
  }
}

export async function getBuilds({
  visibility = "all",
  includeOwn = false,
  cache = "default",
}: BuildOptions = {}) {
  const supabase = await getServerClient();

  // Get current user if authenticated
  const {
    data: { user },
  } = await supabase.auth.getUser();

  let query = supabase.from("builds").select("*");

  // Optimize query based on visibility and user status
  if (user) {
    if (visibility === "all") {
      query = query.or(`visibility.eq.public,user_id.eq.${user.id}`);
    } else if (visibility === "private" || visibility === "unlisted") {
      query = query.eq("visibility", visibility).eq("user_id", user.id);
    } else {
      query = query.eq("visibility", "public");
      if (includeOwn) {
        query = query.or(`user_id.eq.${user.id}`);
      }
    }
  } else {
    // Non-authenticated users only see public builds
    query = query.eq("visibility", "public");
  }

  // Cache headers are handled by Next.js page config
  const { data, error } = await query.order("created_at", { ascending: false });

  if (error) throw error;
  return data || [];
}

export async function createBuild(build: CreateBuildData) {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  const baseSlug = generateSlug(build.name);
  const uniqueSlug = `${baseSlug}-${Math.random().toString(36).substring(2, 10)}`;

  const { data, error } = await supabase
    .from("builds")
    .insert([{ ...build, user_id: user.id, slug: uniqueSlug }])
    .select()
    .single();

  if (error) throw error;

  await logActivity(
    "build",
    "Created new build",
    `Started a new ${build.poe_class || ""} build template`,
    { buildId: data.id, buildName: build.name }
  );

  // Only revalidate necessary paths
  revalidatePath("/build-planner");
  return data;
}

export async function updateBuild(id: string, updates: UpdateBuildData) {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  const foundBuild = await findBuild(supabase, id);
  if (!foundBuild || foundBuild.user_id !== user.id) {
    throw new Error("Build not found or unauthorized");
  }

  let updatedFields = { ...updates };
  if (updates.name) {
    const baseSlug = generateSlug(updates.name);
    updatedFields.slug = `${baseSlug}-${Math.random().toString(36).substring(2, 10)}`;
  }

  const { data, error } = await supabase
    .from("builds")
    .update(updatedFields)
    .eq("id", foundBuild.id)
    .select()
    .single();

  if (error) throw new Error(`Failed to update build: ${error.message}`);
  if (!data) throw new Error("Failed to update build: No data returned");

  await logActivity("build", "Updated build", `Made changes to ${foundBuild.name}`, {
    buildId: id,
    updates,
  });

  // Only revalidate current build path and list
  revalidatePath("/build-planner");
  revalidatePath(`/build-planner/${data.slug || data.id}`);

  return data;
}

export async function deleteBuild(id: string) {
  const supabase = await getServerClient();

  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Not authenticated");

  const foundBuild = await findBuild(supabase, id);
  if (!foundBuild || foundBuild.user_id !== user.id) {
    throw new Error("Not authorized to delete this build");
  }

  const { error } = await supabase.from("builds").delete().eq("id", foundBuild.id);

  if (error) throw error;

  await logActivity("build", "Deleted build", `Removed ${foundBuild.name}`, { buildId: id });

  // Only revalidate list path
  revalidatePath("/build-planner");

  return true;
}

