"use server";

import { revalidatePath } from "next/cache";

import { getServerClient } from "~/app/_actions/supabase";
import type { Database, VisibilityType } from "~/lib/supabase/types";

// Application currently only supports these types
export type BuildVisibility = Exclude;

export type BuildSettings = {
  defaultVisibility: BuildVisibility;
  autoSync: boolean;
};

// Type guard to ensure visibility is supported
function isValidVisibility(visibility: VisibilityType): visibility is BuildVisibility {
  return visibility === "private" || visibility === "unlisted";
}

// Type to ensure database compatibility
type DatabaseBuildSettings = Database["public"]["Tables"]["profiles"]["Row"]["build_settings"];

export type SettingsUpdateResponse = {
  success: boolean;
  error?: string;
};

export async function updateBuildSettings(
  settings: BuildSettings
): Promise {
  const supabase = await getServerClient();

  const {
    data: { user },
    error: userError,
  } = await supabase.auth.getUser();

  if (userError) {
    return { success: false, error: userError.message };
  }
  if (!user) {
    return { success: false, error: "Not authenticated" };
  }

  const { error: updateError } = await supabase
    .from("profiles")
    .update({
      build_settings: settings,
    })
    .eq("id", user.id);

  if (updateError) {
    return { success: false, error: updateError.message };
  }

  revalidatePath("/profile");
  return { success: true };
}

export async function getBuildSettings(): Promise {
  const supabase = await getServerClient();

  const {
    data: { user },
    error: userError,
  } = await supabase.auth.getUser();

  if (userError || !user) {
    return null;
  }

  const { data, error } = await supabase
    .from("profiles")
    .select("build_settings")
    .eq("id", user.id)
    .single();

  if (error || !data) {
    return null;
  }

  if (data.build_settings) {
    const dbSettings = data.build_settings as DatabaseBuildSettings;
    if (!dbSettings) return null;

    const visibility = dbSettings.defaultVisibility;

    // Convert database settings to application settings
    const settings: BuildSettings = {
      defaultVisibility: isValidVisibility(visibility) ? visibility : "unlisted",
      autoSync: dbSettings.autoSync,
    };

    return settings;
  }
  return null;
}

import type { POEOAuthConfig, POEScope } from "@/types/poe-api";

import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

import { POEClient } from "~/lib/poe/client";
import { createMiddlewareClient } from "~/lib/supabase/actions";

const POE_CONFIG: POEOAuthConfig = {
  clientId: process.env.NEXT_PUBLIC_POE_CLIENT_ID!,
  clientSecret: process.env.POE_CLIENT_SECRET!,
  redirectUri: `${process.env.NEXT_PUBLIC_APP_URL}/api/auth/callback`,
  scopes: ["account:profile", "account:stashes", "account:characters"] as POEScope[],
  isConfidentialClient: true,
};

export async function GET(request: NextRequest) {
  try {
    const requestUrl = new URL(request.url);
    const code = requestUrl.searchParams.get("code");
    const next = requestUrl.searchParams.get("next");
    const type = requestUrl.searchParams.get("type");
    const provider = requestUrl.searchParams.get("provider");

    // Handle POE OAuth callback
    if (provider === "poe" && code) {
      const codeVerifier = request.cookies.get("poe_code_verifier")?.value;
      if (!codeVerifier) {
        throw new Error("Missing code verifier");
      }

      const poeClient = new POEClient(POE_CONFIG);
      await poeClient.exchangeCode(code, codeVerifier);

      // Clear code verifier cookie
      const response = NextResponse.redirect(new URL("/profile", requestUrl.origin));
      response.cookies.set("poe_code_verifier", "", { maxAge: 0 });
      return response;
    }

    // Handle Supabase auth callback
    if (!code) {
      console.error("No code provided in callback");
      return NextResponse.redirect(new URL("/", requestUrl.origin));
    }

    const response = new NextResponse();
    const supabase = createMiddlewareClient(request, response);

    const { error } = await supabase.auth.exchangeCodeForSession(code);

    if (error) {
      console.error("Error exchanging code for session:", error.message);
      return NextResponse.redirect(new URL("/auth/login", requestUrl.origin));
    }

    // Handle different callback types
    if (type === "recovery") {
      const redirectResponse = NextResponse.redirect(
        new URL("/auth/reset-password", requestUrl.origin)
      );
      // Copy cookies from the middleware client response
      response.headers.forEach((value, key) => {
        if (key.toLowerCase() === "set-cookie") {
          redirectResponse.headers.set(key, value);
        }
      });
      return redirectResponse;
    }

    if (type === "email_confirmation") {
      const redirectResponse = NextResponse.redirect(new URL("/profile", requestUrl.origin));
      // Copy cookies from the middleware client response
      response.headers.forEach((value, key) => {
        if (key.toLowerCase() === "set-cookie") {
          redirectResponse.headers.set(key, value);
        }
      });
      return redirectResponse;
    }

    // Default redirect with RSC cache busting
    const redirectTo = next
      ? new URL(next, requestUrl.origin)
      : new URL("/profile", requestUrl.origin);

    // Add timestamp to force a fresh request
    redirectTo.searchParams.set("_t", Date.now().toString());

    const redirectResponse = NextResponse.redirect(redirectTo);

    // Copy cookies from the middleware client response
    response.headers.forEach((value, key) => {
      if (key.toLowerCase() === "set-cookie") {
        redirectResponse.headers.set(key, value);
      }
    });

    // Set cache control headers to prevent stale RSC data
    redirectResponse.headers.set("Cache-Control", "no-store, must-revalidate");
    redirectResponse.headers.set("Pragma", "no-cache");

    return redirectResponse;
  } catch (error) {
    console.error("Callback error:", error);
    return NextResponse.redirect(new URL("/auth/login", request.url));
  }
}

import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

import { createMiddlewareClient } from "~/lib/supabase/actions";

export async function POST(request: NextRequest) {
  try {
    const { email, password } = await request.json();
    const response = new NextResponse();
    const supabase = createMiddlewareClient(request, response);

    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      return NextResponse.json({ error: error.message }, { status: 400 });
    }

    const jsonResponse = NextResponse.json({
      user: data.user,
      session: data.session,
    });

    // Copy cookies from the middleware client response to our json response
    response.headers.forEach((value, key) => {
      if (key.toLowerCase() === "set-cookie") {
        jsonResponse.headers.set(key, value);
      }
    });

    return jsonResponse;
  } catch (error) {
    console.error("Login error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

// Handle OPTIONS for CORS
export async function OPTIONS() {
  return new NextResponse(null, {
    status: 204,
    headers: {
      "Access-Control-Allow-Credentials": "true",
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "GET,POST,OPTIONS",
      "Access-Control-Allow-Headers": "Content-Type, Authorization",
    },
  });
}

import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

import { createMiddlewareClient } from "~/lib/supabase/actions";

export async function POST(request: NextRequest) {
  try {
    const { email } = await request.json();
    const response = new NextResponse();
    const supabase = createMiddlewareClient(request, response);

    const { error } = await supabase.auth.resetPasswordForEmail(email, {
      redirectTo: `${request.nextUrl.origin}/api/auth/callback?type=recovery`,
    });

    if (error) {
      return NextResponse.json({ error: error.message }, { status: 400 });
    }

    const jsonResponse = NextResponse.json({
      message: "Password reset email sent successfully",
    });

    // Copy cookies from the middleware client response to our json response
    response.headers.forEach((value, key) => {
      if (key.toLowerCase() === "set-cookie") {
        jsonResponse.headers.set(key, value);
      }
    });

    return jsonResponse;
  } catch (error) {
    console.error("Password reset error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

// Handle OPTIONS for CORS
export async function OPTIONS() {
  return new NextResponse(null, {
    status: 204,
    headers: {
      "Access-Control-Allow-Credentials": "true",
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "GET,POST,OPTIONS",
      "Access-Control-Allow-Headers": "Content-Type, Authorization",
    },
  });
}

import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

import { createMiddlewareClient } from "~/lib/supabase/actions";

export async function POST(request: NextRequest) {
  try {
    const { email, password } = await request.json();
    const response = new NextResponse();
    const supabase = createMiddlewareClient(request, response);

    const baseUrl = process.env.NODE_ENV === "production" 
      ? "https://poe2.dev"
      : request.nextUrl.origin;

    const { data, error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        emailRedirectTo: `${baseUrl}/api/auth/callback`,
        data: {
          email_verified: false,
        },
      },
    });

    if (error) {
      return NextResponse.json({ error: error.message }, { status: 400 });
    }

    const jsonResponse = NextResponse.json({
      user: data.user,
      session: data.session,
    });

    // Copy cookies from the middleware client response to our json response
    response.headers.forEach((value, key) => {
      if (key.toLowerCase() === "set-cookie") {
        jsonResponse.headers.set(key, value);
      }
    });

    return jsonResponse;
  } catch (error) {
    console.error("Signup error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

// Handle OPTIONS for CORS
export async function OPTIONS() {
  return new NextResponse(null, {
    status: 204,
    headers: {
      "Access-Control-Allow-Credentials": "true",
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "GET,POST,OPTIONS",
      "Access-Control-Allow-Headers": "Content-Type, Authorization",
    },
  });
}

import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

export async function GET() {
  try {
    return NextResponse.json({
      success: true,
      message: "Hello from the API!",
      timestamp: new Date().toISOString(),
    });
  } catch (error) {
    return NextResponse.json(
      {
        success: false,
        error: error instanceof Error ? error.message : "An unexpected error occurred",
      },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();

    return NextResponse.json({
      success: true,
      message: "Data received successfully",
      data: body,
      timestamp: new Date().toISOString(),
    });
  } catch (error) {
    return NextResponse.json(
      {
        success: false,
        error: error instanceof Error ? error.message : "An unexpected error occurred",
      },
      { status: 500 }
    );
  }
}

import { revalidateTag } from "next/cache";
import { NextRequest, NextResponse } from "next/server";

export async function POST(request: NextRequest) {
  try {
    const { tag } = await request.json();

    if (!tag) {
      return NextResponse.json({ message: "Missing tag parameter" }, { status: 400 });
    }

    // Revalidate the cache for the specified tag
    revalidateTag(tag);

    return NextResponse.json({ revalidated: true, now: Date.now() }, { status: 200 });
  } catch (error) {
    return NextResponse.json({ message: "Error revalidating", error }, { status: 500 });
  }
}

import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

import { createMiddlewareClient } from "~/lib/supabase/actions";

export async function PATCH(request: NextRequest) {
  const response = new NextResponse();
  const supabase = createMiddlewareClient(request, response);

  try {
    const {
      data: { user },
    } = await supabase.auth.getUser();
    if (!user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const body = await request.json();
    const { avatar_url } = body;

    const { data: updateData, error: updateError } = await supabase.auth.updateUser({
      data: { avatar_url },
    });

    if (updateError) throw updateError;

    // Create a new response with the updated data
    const jsonResponse = NextResponse.json({
      success: true,
      user: updateData.user,
    });

    // Copy cookies from the middleware client response to the new response
    const middlewareResponse = response.headers;
    middlewareResponse.forEach((value, key) => {
      if (key.toLowerCase() === "set-cookie") {
        jsonResponse.headers.set(key, value);
      }
    });

    return jsonResponse;
  } catch (error) {
    console.error("Update user error:", error);
    return NextResponse.json(
      { error: error instanceof Error ? error.message : "Internal server error" },
      { status: 500 }
    );
  }
}

export async function DELETE(request: NextRequest) {
  const response = new NextResponse();
  const supabase = createMiddlewareClient(request, response);

  try {
    const {
      data: { user },
    } = await supabase.auth.getUser();
    if (!user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Delete builds and related data
    const { error: buildsError } = await supabase.from("builds").delete().eq("user_id", user.id);

    if (buildsError) throw buildsError;

    // Delete equipment
    const { error: equipmentError } = await supabase
      .from("equipment")
      .delete()
      .eq("user_id", user.id);

    if (equipmentError) throw equipmentError;

    // Delete skill gems
    const { error: skillGemsError } = await supabase
      .from("skill_gems")
      .delete()
      .eq("user_id", user.id);

    if (skillGemsError) throw skillGemsError;

    // Delete build configs
    const { error: buildConfigsError } = await supabase
      .from("build_configs")
      .delete()
      .eq("user_id", user.id);

    if (buildConfigsError) throw buildConfigsError;

    // Delete profile (which will trigger auth.users deletion via our function)
    const { error: profileError } = await supabase.from("profiles").delete().eq("id", user.id);

    if (profileError) throw profileError;

    // Copy cookies from the middleware client response
    response.headers.forEach((value, key) => {
      if (key.toLowerCase() === "set-cookie") {
        response.headers.set(key, value);
      }
    });

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error("Delete user error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

