  params: Promise<{ id: string }>;
}

export default async function BuildPlannerLayout({ children, params }: BuildPlannerLayoutProps) {
  // Check auth first
  const supabase = await getServerClient();
  const {
    data: { user },
  } = await supabase.auth.getUser();

  // Await params before using
  const { id } = await params;

  // Get build data
  const { data: build, error } = await getBuild(id).then(
    (build) => ({ data: build, error: null }),
    (error) => ({ data: null, error })
  );

  // Handle loading and error states
  if (!build) {
    return (






    );
  }

  // Handle errors after loading
  if (error) {
    return (






    );
  }

  // Handle slug redirects
  if (build.slug && id !== build.slug) {
    redirect(`/build-planner/${build.slug}`);
  }

  // Get current path for nav and edit check
  const pathBase = build.slug || build.id;
  const childrenStr = String(children || "");
  const isEditPage = childrenStr.includes("EditBuildForm") || childrenStr.includes("edit/page");

  // Determine current path based on children content
  let currentPath = `/build-planner/${pathBase}`;
  if (isEditPage) {
    currentPath = `/build-planner/${pathBase}/edit`;
    // Check auth for edit page
    if (!user) {
      redirect("/auth/login");
    }
    if (build.user_id !== user.id) {
      redirect("/build-planner");
    }
  } else if (childrenStr.includes("equipment")) {
    currentPath = `/build-planner/${pathBase}/equipment`;
  } else if (childrenStr.includes("skills")) {
    currentPath = `/build-planner/${pathBase}/skills`;
  } else if (childrenStr.includes("stats")) {
    currentPath = `/build-planner/${pathBase}/stats`;
  } else if (childrenStr.includes("notes")) {
    currentPath = `/build-planner/${pathBase}/notes`;
  } else if (childrenStr.includes("import-export")) {
    currentPath = `/build-planner/${pathBase}/import-export`;
  }

  const content = (

      <Suspense fallback={
}>







        <Suspense fallback={
}>
          {children}



  );

  return (


      {content}

  );
}

import { Metadata, ResolvingMetadata } from "next";

import { generateDynamicMetadata } from "~/utils/metadata";

export async function generateMetadata(
  { params }: { params: Promise<{ id: string }> | undefined },
  parent: ResolvingMetadata
): Promise {
  if (!params) {
    return {
      title: "Build Not Found",
      description: "The requested build could not be found",
    };
  }

  try {
    const { id } = await params;
    // TODO: Replace with actual build service call when implemented
    const build = {
      id,
      name: "Example Build",
      description: "A detailed Path of Exile 2 character build configuration.",
      author: "POE2 Tools User",
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
      class: "Ranger",
      level: 90,
      tags: ["Bow", "Critical Strike", "Elemental"],
      views: 1000,
      likes: 50,
    };

    return generateDynamicMetadata({ params: { id } }, parent, {
      title: `${build.name} (${build.class}) - POE2 Build`,
      description: `Level ${build.level} ${build.class} build. ${build.description}`,
      path: `/build-planner/${build.id}`,
      openGraph: {
        type: "website",
        publishedTime: build.createdAt,
        authors: [build.author],
      },
      schema: {
        type: "build",
        data: {
          ...build,
          applicationCategory: "Game Build Configuration",
          keywords: [
            "Path of Exile 2",
            "POE2",
            build.class,
            ...build.tags,
            "Build Guide",
            "Character Build",
          ],
          interactionStatistic: [
            {
              "@type": "InteractionCounter",
              interactionType: "https://schema.org/ViewAction",
              userInteractionCount: build.views,
            },
            {
              "@type": "InteractionCounter",
              interactionType: "https://schema.org/LikeAction",
              userInteractionCount: build.likes,
            },
          ],
        },
        breadcrumbs: [
          { name: "Home", path: "/" },
          { name: "Build Planner", path: "/build-planner" },
          { name: build.class, path: `/build-planner?class=${build.class.toLowerCase()}` },
          { name: build.name, path: `/build-planner/${build.id}` },
        ],
      },
    });
  } catch (error) {
    console.error("Error generating build metadata:", error);
    return {};
  }
}

