import type { MetadataRoute } from "next";

import { ascendanciesWithMeta } from "~/lib/ascendancies/data";
import { guides } from "~/lib/guides/data";
import { mechanics } from "~/lib/mechanics/data";
import { NewsService } from "~/services/news-service";

type SitemapEntry = {
  url: string;
  lastModified: Date;
  changeFrequency: MetadataRoute.Sitemap[number]["changeFrequency"];
  priority: number;
};

export default async function sitemap(): Promise {
  const baseUrl = "https://poe2.dev";
  const defaultDate = new Date();

  // Get all news items including patch notes
  const allNews = await NewsService.getAllNews();

  const routes: SitemapEntry[] = [
    // Core pages
    {
      url: baseUrl,
      lastModified: defaultDate,
      changeFrequency: "daily",
      priority: 1.0,
    },
    // Tools
    {
      url: `${baseUrl}/build-planner`,
      lastModified: defaultDate,
      changeFrequency: "daily",
      priority: 0.9,
    },
    {
      url: `${baseUrl}/calculators`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.9,
    },
    {
      url: `${baseUrl}/calculators/dps`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.8,
    },
    {
      url: `${baseUrl}/calculators/speed`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.8,
    },
    {
      url: `${baseUrl}/calculators/currency`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.8,
    },
    {
      url: `${baseUrl}/builds`,
      lastModified: defaultDate,
      changeFrequency: "daily",
      priority: 0.9,
    },
    {
      url: `${baseUrl}/skill-tree`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.8,
    },
    // Content pages
    {
      url: `${baseUrl}/news`,
      lastModified: defaultDate,
      changeFrequency: "daily",
      priority: 0.8,
    },
    {
      url: `${baseUrl}/news/patch-notes`,
      lastModified: defaultDate,
      changeFrequency: "daily",
      priority: 0.8,
    },
    {
      url: `${baseUrl}/guides`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.7,
    },
    {
      url: `${baseUrl}/mechanics`,
      lastModified: defaultDate,
      changeFrequency: "monthly",
      priority: 0.6,
    },
    {
      url: `${baseUrl}/ascendancies`,
      lastModified: defaultDate,
      changeFrequency: "monthly",
      priority: 0.7,
    },
    // Legal & Info pages
    {
      url: `${baseUrl}/privacy`,
      lastModified: defaultDate,
      changeFrequency: "monthly",
      priority: 0.3,
    },
    {
      url: `${baseUrl}/terms`,
      lastModified: defaultDate,
      changeFrequency: "monthly",
      priority: 0.3,
    },
    // Build planner sections
    {
      url: `${baseUrl}/build-planner/equipment`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.6,
    },
    {
      url: `${baseUrl}/build-planner/skills`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.6,
    },
    {
      url: `${baseUrl}/build-planner/stats`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.6,
    },
    {
      url: `${baseUrl}/build-planner/notes`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.6,
    },
    {
      url: `${baseUrl}/build-planner/import-export`,
      lastModified: defaultDate,
      changeFrequency: "weekly",
      priority: 0.6,
    },
  ];

  // Add mechanics pages
  const mechanicsRoutes: SitemapEntry[] = Object.keys(mechanics).map((slug) => ({
    url: `${baseUrl}/mechanics/${slug}`,
    lastModified: defaultDate,
    changeFrequency: "monthly",
    priority: 0.6,
  }));

  // Add guide pages
  const guideRoutes: SitemapEntry[] = Object.keys(guides).map((slug) => ({
    url: `${baseUrl}/guides?category=${slug}`,
    lastModified: defaultDate,
    changeFrequency: "weekly",
    priority: 0.7,
  }));

  // Add news pages with proper routing
  const newsRoutes: SitemapEntry[] = allNews.map((news) => {
    // Safely handle date parsing
    let lastMod = defaultDate;
    try {
      lastMod = new Date(news.date);
    } catch (e) {
      console.error("Error parsing date:", e);
    }

    const path =
      news.type === "patch-note"
        ? `/news/patch-notes/${news.slug || news.id}`
        : `/news/${news.slug || news.id}`;

    return {
      url: `${baseUrl}${path}`,
      lastModified: lastMod,
      changeFrequency: news.type === "patch-note" ? "daily" : "weekly",
      priority: news.type === "patch-note" ? 0.8 : 0.7,
    };
  });

  // Add ascendancy pages
  const ascendancyRoutes: SitemapEntry[] = ascendanciesWithMeta.map((ascendancy) => ({
    url: `${baseUrl}/ascendancies/${ascendancy.id}`,
    lastModified: defaultDate,
    changeFrequency: "monthly",
    priority: 0.7,
  }));

  return [...routes, ...mechanicsRoutes, ...guideRoutes, ...newsRoutes, ...ascendancyRoutes];
}

import { type NextRequest, NextResponse } from "next/server";

import { updateSession } from "./lib/supabase/middleware";

// Rate limits per endpoint (requests per 5 minutes)
const RATE_LIMITS = {
  "/api/auth/login": 30,
  "/api/auth/signup": 10,
  "/api/auth/reset-password": 5,
  "/api/auth/refresh-token": 100,
  "/api/builds/": 300,
  "/api/search/": 200,
  "/api/user/": 100,
  "/api/webhooks/": 1000,
  "/api/revalidate/": 50,
  "/api/": 500,
  "/*": 2000,
};

const ALLOWED_ORIGINS = [
  "https://poe2.tools",
  "https://www.poe2.tools",
  process.env.NEXT_PUBLIC_SITE_URL,
  process.env.NODE_ENV === "development" ? "http://localhost:3000" : null,
].filter(Boolean);

const requestCounts = new Map();

function getRateLimit(path: string): number {
  return (
    Object.entries(RATE_LIMITS).find(([pattern]) => path.startsWith(pattern))?.[1] ||
    RATE_LIMITS["/*"]
  );
}

function checkRateLimit(request: NextRequest): {
  allowed: boolean;
  headers: Record;
} {
  const identifier =
    request.headers.get("x-forwarded-for") ||
    request.headers.get("x-real-ip") ||
    new URL(request.url).host;

  const now = Date.now();
  const windowStart = now - 5 * 60 * 1000;

  for (const [key, value] of requestCounts.entries()) {
    if (value.timestamp < windowStart) requestCounts.delete(key);
  }

  const current = requestCounts.get(identifier) || { count: 0, timestamp: now };

  if (current.timestamp < windowStart) {
    current.count = 0;
    current.timestamp = now;
  }

  current.count++;
  requestCounts.set(identifier, current);

  const limit = getRateLimit(request.nextUrl.pathname);
  const remaining = Math.max(0, limit - current.count);
  const reset = Math.ceil((current.timestamp + 5 * 60 * 1000 - now) / 1000);

  return {
    allowed: current.count <= limit,
    headers: {
      "X-RateLimit-Limit": limit.toString(),
      "X-RateLimit-Remaining": remaining.toString(),
      "X-RateLimit-Reset": reset.toString(),
    },
  };
}

function handleCORS(request: NextRequest, response: NextResponse): void {
  const origin = request.headers.get("origin");

  if (origin && ALLOWED_ORIGINS.includes(origin)) {
    response.headers.set("Access-Control-Allow-Origin", origin);
  }

  if (request.method === "OPTIONS") {
    response.headers.set("Access-Control-Allow-Methods", "GET,POST,PUT,DELETE,OPTIONS");
    response.headers.set(
      "Access-Control-Allow-Headers",
      "Content-Type, Authorization, X-CSRF-Token"
    );
    response.headers.set("Access-Control-Max-Age", "86400");
  }

  response.headers.set("Access-Control-Allow-Credentials", "true");
}

export async function middleware(request: NextRequest) {
  let response = NextResponse.next();

  try {
    // Handle CORS
    handleCORS(request, response);
    if (request.method === "OPTIONS") return response;

    // Apply rate limiting for API and dynamic routes
    const { allowed, headers } = checkRateLimit(request);
    if (!allowed) {
      return new NextResponse("Too Many Requests", {
        status: 429,
        headers: { ...headers, "Retry-After": headers["X-RateLimit-Reset"] },
      });
    }
    Object.entries(headers).forEach(([key, value]) => {
      response.headers.set(key, value);
    });

    // Update auth session and handle protected routes
    response = await updateSession(request);

    // Add security headers for HTML responses
    if (request.headers.get("accept")?.includes("text/html")) {
      const securityHeaders = {
        "Content-Security-Policy": [
          "default-src 'self'",
          "script-src 'self' 'unsafe-inline' 'unsafe-eval' gc.zgo.at *.goatcounter.com *.vercel.live vercel.live",
          "style-src 'self' 'unsafe-inline'",
          "img-src 'self' data: https: blob: *.fextralife.com",
          "font-src 'self' data:",
          "connect-src 'self' https: wss:",
          "frame-ancestors 'none'",
          "base-uri 'self'",
          "form-action 'self'",
          "worker-src 'self' blob:",
          "manifest-src 'self'",
          "upgrade-insecure-requests",
        ].join("; "),
      };

      Object.entries(securityHeaders).forEach(([key, value]) => {
        response.headers.set(key, value);
      });
    }

    return response;
  } catch (error) {
    console.error("Middleware error:", error);
    return request.nextUrl.pathname.startsWith("/api/")
      ? NextResponse.json({ error: "Internal Server Error" }, { status: 500 })
      : NextResponse.next();
  }
}

export const config = {
  matcher: [
    /*
     * Match specific routes that need middleware:
     * - Root pages
     * - Auth pages
     * - Build planner root (but not subroutes)
     * - Other top-level pages
     */
    "/",
    "/auth/:path*",
    "/build-planner",
    "/guides/:path*",
    "/news/:path*",
    "/mechanics/:path*",
    "/profile",
    "/terms",
    "/privacy",

    /*
     * Exclude:
     * - API routes
     * - Static assets
     * - Build planner subroutes
     * - Image files
     * - Manifest files
     */
    "/((?!api|_next/static|_next/image|favicon.ico|build-planner/.*|.*\\.(?:svg|png|jpg|jpeg|gif|webp)$|manifest\\.json|robots\\.txt|sitemap\\.xml|icon\\.svg).*)",
  ],
};

